using UnityEngine;public class Player : MonoBehaviour{    private const string AxisNameHorizontal = "Horizontal";    private const string AxisNameVertical = "Vertical";	    public Vector3 currentMovement;    public float moveSpeed = 5;    public CharacterController controller;		void Awake ()	{        if (controller == null)        {            controller = GetComponent<CharacterController>();        }	}	void Update ()
	{
	    currentMovement[0] = Input.GetAxisRaw(AxisNameHorizontal);
        currentMovement[1] = Input.GetAxisRaw(AxisNameVertical);	    controller.Move(currentMovement*moveSpeed*Time.deltaTime);		float y = Input.GetAxis("RSVertical");		float x = Input.GetAxis("RSHorizontal");		if (Mathf.Abs(y) > 0.1f || Mathf.Abs(x) > 0.1f )			transform.localEulerAngles = new Vector3(0,0, (Mathf.Atan2(y, x))*180/(Mathf.PI));		//transform.LookAt(transform.position+new Vector3(Input.GetAxisRaw("RSVertical"), Input.GetAxisRaw("RSHorizontal"),0), Vector3.up);		print (new Vector3(x, y,0));		print (currentMovement);	}}